mapper.select:
select * from (select rownum rnum,e.* from (select * from (select c.*,nvl(total , 0)vote_total from (select B.*, (select count(*) from vote_result_tbl where vote_no = b.vote_no and member_no = #{memberNo})vote_ok from ( select A.* from ( select vote_no,member_nickname , vote_title, to_char(vote_date,'yyyy-mm-dd HH24:MI') as vote_date, to_char(vote_end_date,'yyyy-mm-dd HH24:MI') as vote_end_date , vote_check from member_tbl join vote_tbl using(member_no)
where vote_check = #{order}
where vote_check = #{order}
)A )B )C left join (select count(*)total,vote_no from vote_result_tbl group by vote_no) D on (c.vote_no = d.vote_no)) order by vote_check asc, vote_date desc, vote_no desc)e)f where rnum between #{startRow} and #{endRow}

 	select * from vote_option_tbl
	where vote_no = #{voteNo}
	order by vote_option_no

mapper.update:
update vote_comment_tbl 
set vote_comment_content =  #{voteCommentContent}
where vote_comment_no = #{voteCommentNo}

mapper.select:
SELECT COUNT(*) 
FROM VOTE_COMMENT_REPORT_TBL
WHERE VOTE_COMMENT_NO = #{voteCommentNo}
  AND MEMBER_NO = #{memberNo}

SELECT COUNT(*)
FROM VOTE_LIKE_TBL
WHERE VOTE_NO = #{voteNo}
  AND MEMBER_NO = #{memberNo}

mapper.insert:
INSERT INTO VOTE_LIKE_TBL (
  VOTE_NO, MEMBER_NO, VOTE_LIKE_DATE
) VALUES (
  #{voteNo}, #{memberNo}, SYSDATE
)

mapper.delete:
DELETE FROM VOTE_LIKE_TBL
WHERE VOTE_NO = #{voteNo}
  AND MEMBER_NO = #{memberNo}

mapper.select:
SELECT COUNT(*)
FROM VOTE_REPORT_TBL
WHERE VOTE_NO = #{voteNo}
  AND MEMBER_NO = #{memberNo}
